package id.yongki.layananqta;import androidx.annotation.NonNull;import androidx.appcompat.app.AppCompatActivity;import android.content.Intent;import android.os.Bundle;import android.util.Log;import android.view.View;import android.widget.Button;import android.widget.EditText;import android.widget.ProgressBar;import android.widget.TextView;import android.widget.Toast;import com.google.android.gms.tasks.OnCompleteListener;import com.google.android.gms.tasks.OnFailureListener;import com.google.android.gms.tasks.OnSuccessListener;import com.google.android.gms.tasks.Task;import com.google.firebase.auth.AuthResult;import com.google.firebase.auth.FirebaseAuth;import com.google.firebase.auth.FirebaseUser;public class LoginActivity extends AppCompatActivity {    private FirebaseAuth firebaseAuth;    private ProgressBar progressBar;    private TextView daftar;    private EditText etemail, etPassword;    private Button loginBtn;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_login);        firebaseAuth = FirebaseAuth.getInstance();        progressBar = findViewById(R.id.login_progressbar);        loginBtn = findViewById(R.id.login_loginbtn);        etemail = findViewById(R.id.login_etemail);        etPassword = findViewById(R.id.login_etpassword);        daftar = findViewById(R.id.login_tvregister);        loginBtn.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                progressBar.setVisibility(View.VISIBLE);                String email = etemail.getText().toString();                String password = etPassword.getText().toString();                String semail = getString(R.string.email_kosong);                String spassword = getString(R.string.pass_kosong);                if (email.isEmpty()) {                    progressBar.setVisibility(View.GONE);                    etemail.setError(semail);                } else if (password.isEmpty()) {                    progressBar.setVisibility(View.GONE);                    etPassword.setError(spassword);                } else {                    firebaseAuth.signInWithEmailAndPassword(email, password)                            .addOnSuccessListener(new OnSuccessListener<AuthResult>() {                                @Override                                public void onSuccess(AuthResult authResult) {                                    startActivity(new Intent(getApplicationContext(), ListActivity.class));                                    finish();                                }                            }).addOnFailureListener(new OnFailureListener() {                        @Override                        public void onFailure(@NonNull Exception e) {                            progressBar.setVisibility(View.GONE);                            Toast.makeText(getApplicationContext(), e.getMessage(), Toast.LENGTH_LONG).show();                        }                    });                }            }        });        daftar.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                startActivity(new Intent(getApplicationContext(),RegisterActivity.class));            }        });    }}